mapper.select:
select tran.id, user.name as owner, tran.money, tran.name, tran.expectedDate, cus.name as customerId, tran.stage, tran.type, tran.source, act.name as activityId, con.fullname as contactsId, tran.createBy, tran.createTime, tran.editBy, tran.editTime, tran.description, tran.contactSummary, tran.nextContactTime
from tbl_tran tran join tbl_user user on tran.owner=user.id join tbl_customer cus on tran.customerId=cus.id left join tbl_activity act on tran.activityId=act.id left join tbl_contacts con on tran.contactsId=con.id
where tran.id=#{id}

mapper.update:
update tbl_tran set
    stage=#{stage},
    editBy=#{editBy},
    editTime=#{editTime}
where id=#{id}
